INSERT INTO `sentence` VALUES (3997, '2001考研英语', 'This phenomenon has created serious concerns over the role of smaller economic firms, of national businessmen and over the ultimate stability of the world economy.', '这一现象引起了对小型经济公司、国家商人和世界经济最终稳定的作用的严重关切。');
INSERT INTO `sentence` VALUES (3998, '2001考研英语', 'All these are beneficial, not detrimental, to consumers.', '所有这些对消费者都是有益的，而不是有害的。');
