INSERT INTO `sentence` VALUES (4002, '2001考研英语', 'The mergers of telecom companies, such as WorldCom, hardly seem to bring higher prices for consumers or a reduction in the pace of technical progress.', '世界通信（WorldCom）等电信公司的合并似乎很难给消费者带来更高的价格，或者降低技术进步的步伐。');
INSERT INTO `sentence` VALUES (4003, '2001考研英语', 'On the contrary, the price of communications is coming down fast.', '相反，通信的价格正在迅速下降。');
