INSERT INTO `sentence` VALUES (5641, '2012考研英语二', 'Joe Blow, Joe Palooka.', 'Joe Blow，Joe Palooka。');
INSERT INTO `sentence` VALUES (5642, '2012考研英语二', 'a working class name.', '工人阶级的名字。');
