INSERT INTO `sentence` VALUES (5983, '2014考研英语一', 'Choose the best word(s) for each numbered blank and mark A, B, C or D on ANSWER SHEET 1.', '为每个编号的空白选择最佳单词，并在答题纸1上标出A、B、C或D。');
