INSERT INTO `sentence` VALUES (6098, '2014考研英语二', 'Teachers,employers,and health professionals have been shown to harbor biases against the obese.', '教师、雇主和卫生专业人员被证明对肥胖有偏见。');
INSERT INTO `sentence` VALUES (6099, '2014考研英语二', 'Answer the questions below each text by choosing A, B, C or D.', '通过选择A、B、C或D回答每个文本下面的问题。');
